mapper.select:
SELECT SUPPLIER_CD, ITEM_NM, SUPPLIER, ITEM_TYPE_CD, ITEM_TYPE_NM, PURCHASE_PRICE FROM T_ITEM_DTL WHERE 1=1
AND ITEM_TYPE_CD = #{itemTypeCd}
AND SUPPLIER_CD = #{supplierCd}
AND ITEM_NM LIKE CONCAT('%', #{itemNm}, '%')
LIMIT #{page}, #{pageSize}

SELECT COUNT(1) FROM T_ITEM_DTL WHERE 1=1
AND ITEM_TYPE_CD = #{itemTypeCd}
AND SUPPLIER_CD = #{supplierCd}
AND ITEM_NM LIKE CONCAT('%', #{itemNm}, '%')
