mapper.select:
SELECT BAR_CODE, ITEM_NM, ITEM_TYPE_CD, ITEM_TYPE_NM FROM T_ITEM_DTL WHERE 1=1
AND ITEM_TYPE_CD = #{itemTypeCd}
AND ITEM_NM LIKE CONCAT('%', #{itemNm}, '%')
LIMIT #{pageNum}, #{pageSize}

SELECT COUNT(1) FROM T_ITEM_DTL WHERE 1=1
AND ITEM_TYPE_CD = #{itemTypeCd}
AND ITEM_NM LIKE CONCAT('%', #{itemNm}, '%')

SELECT DATE_FORMAT(STOCK_DT, '%Y-%m-%d') AS STOCK_DT, BAR_CODE, ITEM_NM, SUPPLIER_CD, SUPPLIER, ITEM_TYPE_CD, ITEM_TYPE_NM, PURCHASE_PRICE, IN_CNT, REMAIN_CNT FROM T_ITEM_STOCK WHERE 1=1
AND ITEM_TYPE_CD = #{itemTypeCd}
AND SUPPLIER_CD = #{supplierCd}
AND ITEM_NM LIKE CONCAT('%', #{itemNm}, '%')
LIMIT #{pageNum}, #{pageSize}

SELECT COUNT(1) FROM T_ITEM_STOCK WHERE 1=1
AND ITEM_TYPE_CD = #{itemTypeCd}
AND SUPPLIER_CD = #{supplierCd}
AND ITEM_NM LIKE CONCAT('%', #{itemNm}, '%')

SELECT DATE_FORMAT(START_DT, '%Y-%m-%d') AS START_DT, DATE_FORMAT(END_DT, '%Y-%m-%d') AS END_DT, BAR_CODE, ITEM_NM, SELL_CNT FROM T_ITEM_SELL WHERE 1=1
AND ITEM_NM LIKE CONCAT('%', #{itemNm}, '%')
LIMIT #{pageNum}, #{pageSize}
